def minimumMovement(obstacleLanes)
    # Write your code here
    n = obstacleLanes.length
    move = 0
    current_position = 2
    lanes = [1,2,3]
    
    0.upto(n - 1) do |i|
     # p obstacleLanes[i]
      if obstacleLanes[i] == current_position
        move += 1
        remaining_track = obstacleLanes[i+1..n-1]
        next_save_position = current_position
        count = 0
        remaining_track.each do |e|
          if e != current_position
            next_save_position = e
            break
          end
          count += 1
        end
        i += count
        new_arr = lanes - [current_position, next_save_position]
        current_position = new_arr[0]
      end                             
    end
   move
end



